package collatz;

import java.math.BigInteger;

public class CollatzInvestigate3aover2b {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//3^1/2^2
		int underA = 1;
		int underB=2;
		
		//3^2/2^3
		int overA=2;
		int overB=3;
		
		int DEPTH = 1000;
		
		BigInteger pow3UnderA = new BigInteger("" + (int)(Math.pow(3, underA)));
		BigInteger pow2UnderB = new BigInteger("" + (int)(Math.pow(2, underB)));
		
		BigInteger pow3OverA = new BigInteger("" + (int)(Math.pow(3, overA)));
		BigInteger pow2OverB = new BigInteger("" + (int)(Math.pow(2, overB)));
		
		System.out.println(pow3UnderA);
		System.out.println(pow2UnderB);

		System.out.println(pow3OverA);
		System.out.println(pow2OverB);
		//TODO: make into a loop
		letsGo(underA, underB, overA, overB, pow3UnderA, pow2UnderB, pow3OverA, pow2OverB, DEPTH);
	}
	
	// alpha = 190537 
	// ( 1 - 3^alpha/2^beta)^-1 = 15 million
	// alpha = 79335 
	// ( 1 - 3^alpha/2^beta)^-1 = 272 K
	//TODO: get more points and plot in excel.

	//
	// 3^10781274 / 2^17087915
	
	//Somehow who went further than me:
	//https://math.stackexchange.com/questions/3266371/a-question-about-the-collatz-conjecture-relation-of-smallest-number-in-a-cycle

	public static void letsGo(int underA, int underB, int overA, int overB, BigInteger pow3UnderA, BigInteger pow2UnderB, BigInteger pow3OverA, BigInteger pow2OverB, int depth) {
		System.out.println(depth);
		if(depth < 0) {
			System.exit(0);
		}
		
		int newA = underA + overA;
		int newB = underB + overB;
		
		BigInteger newPowA = pow3UnderA.multiply(pow3OverA);
		BigInteger newPowB = pow2UnderB.multiply(pow2OverB);
		
		
		boolean newUnder = false;
		if(newPowA.compareTo(newPowB) < 0) {
			newUnder = true;
		}
		
		if(newUnder) {
		
			System.out.println("3^" + newA + " / 2^" +newB);

			letsGo(newA, newB, overA, overB, newPowA, newPowB, pow3OverA, pow2OverB,  depth - 1);
		} else {
			letsGo(underA, underB, newA, newB, pow3UnderA, pow2UnderB, newPowA, newPowB, depth - 1);
		}
	}
	
}

